import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class Home extends Component {
  constructor() {
    super()
    this.state = {
      articles: [],
      totelResults: 0
    }
  }
  async getAPIData() {
    let response = await fetch(`https://newsapi.org/v2/everything?q=${this.props.q}&from=2024-01-24&sortBy=publishedAt&language=${this.props.language}&apiKey=da020cb72f97445ea7269be8a3a980ce`)
    response = await response.json()
    this.setState({
      articles: response.articles,
      totelResults: response.totelResults
    })
  }
  componentDidMount() {
    this.getAPIData()
  }
  componentDidUpdate(oldprops) {
    if (this.props !== oldprops) {
      this.getAPIData()
    }

  }
  render() {
    return (
      <div className='container-fluid'>
        <h5 className='bg-primary text-light text-center p-2 my-2'><span className='text-capitalize'>{this.props.q} </span> News Articles</h5>
        <div className="row">
          {
            this.state.articles.map((item, index) => {
              return <NewsItem
                key={index}
                title={item.title}
                source={item.source.name}
                pic={item.urlToImage}
                date={item.publishedAt}
                description={item.description}
                url={item.url}
              />
            })
          }
        </div>
      </div>
    )
  }
}
