import React, { Component } from 'react'
import InfiniteScroll from 'react-infinite-scroll-component';
import NewsItem from './NewsItem'

export default class Home extends Component {
  constructor() {
    super()
    this.state = {
      articles: [],
      totelResults: 0,
      page: 1
    }
  }
  async getAPIData() {
    let response = await fetch(`https://newsapi.org/v2/everything?&page=${this.state.page}q=${this.props.q}&from=&sortBy=publishedAt&language=${this.props.language}&pageSize=20&apiKey=6100d15b1aba483b9d4b0df2f3c5aba5`)
    response = await response.json()
    this.setState({
      articles: response.articles.filter((x) => x.title !== "[Removed]"),
      totelResults: response.totelResults
    })
  }
  fetchData = async () => {
    this.setState({ page: this.state.page + 1 })
    let response = await fetch(`https://newsapi.org/v2/everything?&page=${this.state.page}q=${this.props.q}&from=&sortBy=publishedAt&language=${this.props.language}&pageSize=20&apiKey=6100d15b1aba483b9d4b0df2f3c5aba5`)
    response = await response.json()
    this.setState({
      articles:this.state.articles.concat(response.articles.filter((x) => x.title !== "[Removed]"))
    })
  }
  componentDidMount() {
    this.getAPIData()
  }
  componentDidUpdate(oldprops) {
    if (this.props !== oldprops) {
      this.getAPIData()
    }

  }
  render() {
    return (
      <div className='container-fluid'>
        <h5 className='bg-primary text-light text-center p-2 my-2'><span className='text-capitalize'>{this.props.q} </span> News Articles</h5>
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchData}
          hasMore={this.state.articles.length < this.state.totelResults}
          loader={<h4>Loading...</h4>}
          endMessage={
            <p style={{ textAlign: 'center' }}>
              <b>NO MORE DATA !!!!</b>
            </p>
          }
        >
          <div className="row">
            {
              this.state.articles.map((item, index) => {
                return <NewsItem
                  key={index}
                  title={item.title}
                  source={item.source.name}
                  pic={item.urlToImage}
                  date={item.publishedAt}
                  description={item.description}
                  url={item.url}
                />
              })
            }
          </div>
        </InfiniteScroll>
      </div>
    )
  }
}
