import React, { Component } from 'react'

export default class Home extends Component {
  constructor() {
    super()
    this.state = {
      articles: [],
      totelResults: 0
    }
  }
  async getAPIData() {
    let response = await fetch("https://newsapi.org/v2/everything?q=All&from=2024-01-24&sortBy=publishedAt&language=hi&apiKey=da020cb72f97445ea7269be8a3a980ce")
    response = await response.json()
    console.log(response)
  }
  componentDidMount(){
    this.getAPIData()
  }
  render() {
    return (
      <div className='container-fluid'>
        <h5 className='bg-primary text-light text-center p-2 my-2'><span className='text-capitalize'>{this.props.q} </span> News Articles</h5>
      </div>
    )
  }
}
